import  Mutation  from '../utils/mutation';

export const checkAdn= async (req, res)=>{
    console.log(req.body)

    let adn_temp = req.body.dna
    try {
        let dna = adn_temp.join("-");
        let status=0
        if(hasMutation(adn_temp)){
            status=1
