        console.error(err.message)
        res.status(200).json({"checkAdn2: ":err.message});
      }
    return;


}
export const getAdn= async (req, res)=>{
    const dnaList =await Adn.aggregate([
        { "$facet": {
          "Total": [
            { "$match" : { "status": { "$exists": true }}},
            { "$count": "Total" },
