let chai = require('chai');
let chaiHttp = require('chai-http');
const expect = require('chai').expect;


chai.use(chaiHttp);
const url= 'http://localhost:3000';
const token='eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyMmViYTM5Mjc5Zjg0NTI2MDcwMjk3NCIsImlhdCI6MTY0NzI5Mjg3MSwiZXhwIjoxNjQ3Mzc5MjcxfQ.mVDvz2oCYxAV_P7--AmknRzeTY5zBtytnjAwMDbn2Wc'

const dna1 ={"dna":["ATGCGA", "CAGTGC", "TTATGT", "AGAAGG", "CCCCTA", "TCACTG"]}
const dna2 ={"dna":["ATGCGA", "CAGTGC", "TTATGT", "AGACGA", "CCCATA", "TCACTG"]}
const invalid={"dna":["ATGCGA", "CAGTGC", "TTZTGT", "AGAAGG", "CCCCTA", "TCACTG"]}

describe('has mutation: ',()=>{
    it('should return  mutant : 1 if its amutation ', (done) => {
    chai.request(url)


    .post('/api/adn/mutation')
    .send(dna1)

    .set('x-access-token', token)
    .end( function(err,res){

    expect(res.body).to.have.property('mutant', 1);
    expect(res).to.have.status(200);

    done();
    });
    });
   });




describe('has mutation: ',()=>{
    it('should return  mutant : 0 if its amutation ', (done) => {
    chai.request(url)


    .post('/api/adn/mutation')
    .send(dna2)

    .set('x-access-token', token)
    .end( function(err,res){
        console.log(res.body)

    expect(res.body).to.have.property('mutant', 0);
    expect(res).to.have.status(403);

    done();
    });
    });
   });


describe('has mutation: ',()=>{
    it('should return  Hay un caracter invalido, solo se permiten los siguientes caracteres: A, T, C, G ', (done) => {
    chai.request(url)


    .post('/api/adn/mutation')
    .send(invalid)

